use eventos;
CREATE TABLE `campos_modelos` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `campo_tipo_id` int(11) NOT NULL,
  `campo` varchar(1000) NOT NULL,
  `obrigatorio` tinyint(1) NOT NULL DEFAULT '0',
  `duplicado` tinyint(1) NOT NULL DEFAULT '1',
  `descricao` varchar(8000) DEFAULT NULL,
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NULL DEFAULT NULL,
  `classe` varchar(250) DEFAULT NULL,
  `autocomplete` tinyint(4) DEFAULT NULL,
  `mascara` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `fk_campos_modelos_tipo_idx` (`campo_tipo_id`),
  CONSTRAINT `fk_campos_modelos_campo_tipo` FOREIGN KEY (`campo_tipo_id`) REFERENCES `campo_tipos` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8;

